package closestrings

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

var result bool

var tests = []struct {
	input1   string
	input2   string
	expected bool
}{
	{
		"abc",
		"bca",
		true,
	},
	{
		"uau",
		"ssx",
		false,
	},
	{
		"a",
		"aa",
		false,
	},
	{
		"aabababababaaa",
		"aaaaaabbaaabbb",
		true,
	},
	{
		"cabbba",
		"abbccc",
		true,
	},
	{
		"abcde",
		"aecdb",
		true,
	},
	{
		"abbzzca",
		"babzzcz",
		false,
	},
	{
		"aabababababaaaaaaaaabbaaabbbccccccccccccdddddddddddddeeeeeeeeeeeeeessssssaaaaaavvvvvvvvvvwwwwwweeeeeeeeggggggg",
		"vvvvaaabbaadddddddeeevvvvvvwwwwwweeeeeeeegggggggaabababababaaaaaaeeeeeeeeeeessssssaaaaaaabbbccccccccccccdddddd",
		true,
	},
	{
		"",
		"",
		false,
	},
}

func TestCloseStrings(t *testing.T) {
	for i, test := range tests {
		t.Log("test ", i+1)

		got := closeStrings(test.input1, test.input2)
		assert.Equal(t, test.expected, got)

		t.Log("got: ", got, ", expected: ", test.expected)

	}
}

func BenchmarkCloseStrings(b *testing.B) {
	funcs := []struct {
		name string
		f    func(s, t string) bool
	}{
		{"mine", closeStrings},
		{"leet-fastest", closeStrings_leet16ms},
	}

	var res bool
	for _, fun := range funcs {

		b.Run(fun.name, func(b *testing.B) {
			b.ReportAllocs()
			b.ResetTimer()
			for k := 0; k < b.N; k++ {
				res = fun.f(tests[k%2].input1, tests[k%2].input2)
			}

		})
	}

	result = res
}
