package decodestring

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

var result string

var tests = []struct {
	input    string
	expected string
}{
	{"3[a]2[bc]", "aaabcbc"},
	{"3[a2[c]]", "accaccacc"},
	{"2[abc]3[cd]ef", "abcabccdcdcdef"},
	{
		"xyz21[er]5[2[abc]3[cd]ef]",
		"xyzerererererererererererererererererererererabcabccdcdcdefabcabccdcdcdefabcabccdcdcdefabcabccdcdcdefabcabccdcdcdef",
	},
	{
		"2[x21[er]]j2[p5[2[ac]31[cd]e]]",
		"",
	},
	{
		"223[a]xyz20[ok210[bc]]",
		"",
	},
}

func TestReverseVowels(t *testing.T) {

	for i, v := range tests {
		t.Log("test: ", i+1)
		got := decodeString(v.input)
		assert.Equal(t, v.expected, got)
	}
}

func BenchmarkReverseVowels(b *testing.B) {
	funcs := []struct {
		name string
		f    func(string) string
	}{
		{"mine", decodeString},
		{"leet-fastest", decodeString_leet0ms},
	}

	var res string
	for _, fun := range funcs {
		b.Run(fun.name, func(b *testing.B) {
			b.ReportAllocs()
			b.ResetTimer()
			for k := 0; k < b.N; k++ {
				res = fun.f(tests[k%len(tests)].input)
			}

		})
	}

	result = res
}
