func TestMaxArea(t *testing.T) {
	for i, v := range tests {
		t.Log("test: ", i+1)
		got := longestSubarrayV2(v.nums)
		assert.Equal(t, v.expected, got)
	}
}

func BenchmarkMaxArea(b *testing.B) {
	funcs := []struct {
		name string
		f    func([]int) int
	}{
		{"mine", longestSubarray},
		{"mine-V2", longestSubarrayV2},
		{"leet-fastest", longestSubarray_leet25ms},
	}

	var res int
	for _, fun := range funcs {

		b.Run(fun.name, func(b *testing.B) {
			b.ReportAllocs()
			b.ResetTimer()
			for k := 0; k < b.N; k++ {
				res = fun.f(tests[k%len(tests)].nums)
			}

		})
	}

	result = res
}
