func TestFindMaxAverage(t *testing.T) {
	for i, v := range tests {
		t.Log("test: ", i+1)
		got := findMaxAverage(v.nums, v.k)
		assert.Equal(t, v.expected, got)
	}
}

func BenchmarkFindMaxAverage(b *testing.B) {
	funcs := []struct {
		name string
		f    func([]int, int) float64
	}{
		{"mine", findMaxAverage},
		{"leet-fastest", findMaxAverage_leet138ms},
	}

	var res float64
	for _, fun := range funcs {

		b.Run(fun.name, func(b *testing.B) {
			b.ReportAllocs()
			b.ResetTimer()
			for k := 0; k < b.N; k++ {
				res = fun.f(tests[k%len(tests)].nums, tests[k%len(tests)].k)
			}
		})
	}

	result = res
}
