package longestpalindrome

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

var result int

func TestLongestPalindrome(t *testing.T) {
	tests := []struct {
		input string
		want  int
	}{
		{"abccccdd", 7},
		{"a", 1},
		{"abcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyz", 52},
		{"abcdefghjklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyz", 51},
		{"abcdefghjklmnopqrstuvwxyzabcdefghijklmnopqrsuvwxyz", 49},
		{"abcdefghijklmnopqrstuvwxyz", 1},
		{"abcdefghjklmnopqrstuvwxyz", 1},
	}

	for _, v := range tests {
		t.Log("Input: ", v.input)
		got := longestPalindrome(v.input)
		assert.Equal(t, v.want, got)
	}
}

func BenchmarkLongestPalindrome(b *testing.B) {
	inputs := []string{
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"abccccdd",
		"a",
		"abcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyz",
		"abcdefghijklmnopqrstuvwxyz",
	}

	l := len(inputs)

	funcs := []struct {
		name string
		f    func(s string) int
	}{
		{"mine", longestPalindrome},
		{"leet-0ms", longestPalindrome_leet0ms},
		//{"leet-4ms", longestPalindrome4ms},
	}

	var res int
	for _, fun := range funcs {
		b.Run(fun.name, func(b *testing.B) {
			b.ReportAllocs()
			b.ResetTimer()
			for k := 0; k < b.N; k++ {
				res = fun.f(inputs[k%l])
			}

		})
	}

	result = res
}
