func TestLongestPalindromeV1(t *testing.T) {
	tests := []struct {
		input string
		want  string
	}{
		{"kajsnoiewpwerjfpwefnkjcaacaacaacj", "jcaacaacaacj"},
		{"bahgfdabababahgfdbad", "abababa"},
		{"babad", "aba"},
		{"cbbd", "bb"},
		{"a", "a"},
		{"ac", "a"},
		{"acc", "cc"},
		{"acac", "aca"},
	}

	for _, v := range tests {
		t.Log("Input: ", v.input)
		got := longestPalindromeV1(v.input)
		assert.Equal(t, v.want, got)
	}
}

